import { FormatedDt } from '../types/types'

let month: number
export const FormatDate = (StringDate: string) : FormatedDt => {
    const d1 = new Date(StringDate)
    const date = d1.getDate()
    month = d1.getMonth()
    const year = d1.getFullYear()
    return {date: `${date}`, month: `${month}`,year :`${year}`}
}
