import styled from 'styled-components'

export const SideBarcss = styled.div`
width: 300px;
height: auto;
background-color:  #4120A9;
@media only screen and (max-width: 600px) {
width:100vw;    
display: flex;
font-size:1rem;
}
`

export const LinkStyle = {
    color: 'white',
    textDecoration : 'none',
}
