import React, { createContext, useContext } from 'react'
import { data } from './types/types'

const Appdata : data = {
    Income : 20000,
    categories : [
        {title: 'First Aid', click: false, total: 0},
        {title: 'Automobile', click: false, total: 0},
        {title: 'Transport', click: false, total: 0},
        {title: 'Food', click: false, total: 0},
        {title: 'Rent', click: false, total: 0},
        {title: 'Gifts', click: false, total: 0},
        {title: 'Child Care', click: false, total: 0},
        {title: 'Clothing', click: false, total: 0},
        {title: 'Utilities', click: false, total: 0}
    ]
}

const DataContext = createContext(Appdata)

export const DataContextProvider = ({children} : React.PropsWithChildren<{}>) => {

    return (
      <DataContext.Provider value={Appdata}>
        {children}
      </DataContext.Provider>
    );
  };

  export const UseAppState = () : data => {
    return useContext(DataContext)
}
