import React, { useState } from 'react'
import { UseAppState } from '../appContext'
import { FaClinicMedical,
         FaCar,
         FaTruck,
         FaHamburger,
         FaHome,
         FaGift,
         FaChild,
         FaTshirt,
         FaNewspaper } from "react-icons/fa";

import { CategoryStyle } from './CategoryStyle'
import { categoryProps } from '../types/types'
import InputModal  from './InputModal'
import './category.css'



function Categories(props: categoryProps) { 
const [modalopen, setModalopen] = useState(false)
const {text} = props
const {state, dispatch} = UseAppState()

  const handleModal = (e : React.MouseEvent,show : boolean)=> {

    // alert(`WHat the hell ${e}`);
     setModalopen(false)
     console.log('Button clicked',show, modalopen)
      // dispatch({type:'category_click',payload: {text: txt} })
  }
  return (
        <CategoryStyle onClick = { (e) => setModalopen(true) }>
          {
            text === 'First Aid' ? <FaClinicMedical/> :
            text === 'Automobile' ? <FaCar /> :
            text === 'Transport' ? <FaTruck /> :
            text === 'Food' ? <FaHamburger /> :
            text === 'Rent' ? <FaHome /> :
            text === 'Gifts' ? <FaGift /> :
            text === 'Child Care' ? <FaChild /> :
            text === 'Clothing' ? <FaTshirt /> :
            <FaNewspaper/>
          }

        <span className='categoryName'>  { text } </span>
        {
          <InputModal showMod = {modalopen} handleMod ={handleModal} />
        }
        </CategoryStyle>
  )
}

export default Categories
