import styled from 'styled-components'

export const CategoryStyle = styled.div`
margin: 0px 0px;
color : white; 
width : 98%;
height: 100%;
background-color: #A16AE8;
font-size: x-large;
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
cursor: pointer;
transition: 0.3s;
&:hover{
    color: brown;
}
`
